import React, { useEffect, useState } from "react";
import styled from "styled-components";
import TransactionItem from "./TransactionItem";

const Container = styled.div``;

const Heading = styled.h2`
  font-size: 25px;
  font-weight: 600;
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 15px 20px;
  outline: none;
  border-radius: 5px;
  margin: 5px 0;
  border: 1px solid #e6e8e9;
  background-color: #e6e8e9;
  margin-bottom: 25px;
`;

const FilterContainer = styled.div`
  display: flex;
  justify-content: space-between;
  gap: 10px;
  margin-bottom: 25px;
`;

const Select = styled.select`
  flex: 1;
  padding: 15px 20px;
  outline: none;
  border-radius: 5px;
  border: 1px solid #e6e8e9;
  background-color: #e6e8e9;
`;

const DateInput = styled.input`
  flex: 1;
  padding: 15px 20px;
  outline: none;
  border-radius: 5px;
  border: 1px solid #e6e8e9;
  background-color: #e6e8e9;
`;

const TransactionItems = styled.div``;

const TransactionsContainer = ({ transactions, removeTransaction }) => {
  const [searchInput, setSearchInput] = useState("");
  const [categoryFilter, setCategoryFilter] = useState("");
  const [dateFilter, setDateFilter] = useState("");
  const [filteredTransactions, setFilteredTransactions] = useState(transactions);

  const filteredData = () => {
    let filtered = transactions;

    if (searchInput) {
      filtered = filtered.filter((item) =>
        item.details.toLowerCase().includes(searchInput.toLowerCase().trim())
      );
    }

    if (categoryFilter) {
      filtered = filtered.filter((item) => item.category === categoryFilter);
    }

    if (dateFilter) {
      filtered = filtered.filter((item) => item.date === dateFilter);
    }

    setFilteredTransactions(filtered);
  };

  useEffect(() => {
    filteredData();
  }, [transactions, searchInput, categoryFilter, dateFilter]);

  return (
    <Container>
      <Heading>Transactions</Heading>

      <SearchInput
        type="text"
        placeholder="Search here"
        value={searchInput}
        onChange={(e) => setSearchInput(e.target.value)}
      />

      <FilterContainer>
        <Select value={categoryFilter} onChange={(e) => setCategoryFilter(e.target.value)}>
          <option value="">All Categories</option>
          <option value="Credited">Credited</option>
          <option value="Food">Food</option>
          <option value="Transport">Transport</option>
          <option value="Shopping">Shopping</option>
          <option value="Entertainment">Entertainment</option>
          <option value="Education">Education</option>
          <option value="Other">Other</option>
        </Select>

        <DateInput
          type="date"
          value={dateFilter}
          onChange={(e) => setDateFilter(e.target.value)}
        />
      </FilterContainer>

      <TransactionItems>
        {filteredTransactions?.length ? (
          filteredTransactions.map((transaction) => (
            <TransactionItem
              transaction={transaction}
              key={transaction.id}
              removeTransaction={removeTransaction}
            />
          ))
        ) : (
          <p>No Transactions</p>
        )}
      </TransactionItems>
    </Container>
  );
};

export default TransactionsContainer;
