import React, { useState } from "react";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 25px;
`;

const Balance = styled.h2`
  font-weight: 500;
  & span {
    font-weight: bold;
  }
`;

const AddBtn = styled.button`
  cursor: pointer;
  background-color: rgb(68, 230, 16);
  color: rgb(255, 255, 255);
  padding: 7px 20px;
  font-size: 16px;
  border: none;
  text-transform: uppercase;
  border-radius: 5px;
`;

const OverviewComponent = ({ toggle, setToggle, income, expense, transactions, setFilteredTransactions }) => {
  const [categoryFilter, setCategoryFilter] = useState("");
  const [startDateFilter, setStartDateFilter] = useState("");
  const [endDateFilter, setEndDateFilter] = useState("");

  const handleCategoryFilterChange = (e) => {
    const category = e.target.value;
    setCategoryFilter(category);
    filterTransactions(category, startDateFilter, endDateFilter);
  };

  const handleStartDateFilterChange = (e) => {
    const startDate = e.target.value;
    setStartDateFilter(startDate);
    filterTransactions(categoryFilter, startDate, endDateFilter);
  };

  const handleEndDateFilterChange = (e) => {
    const endDate = e.target.value;
    setEndDateFilter(endDate);
    filterTransactions(categoryFilter, startDateFilter, endDate);
  };

  const filterTransactions = (category, startDate, endDate) => {
    let filtered = transactions;

    if (category) {
      filtered = filtered.filter((transaction) => transaction.category.toLowerCase() === category.toLowerCase());
    }

    if (startDate) {
      filtered = filtered.filter((transaction) => new Date(transaction.date) >= new Date(startDate));
    }

    if (endDate) {
      filtered = filtered.filter((transaction) => new Date(transaction.date) <= new Date(endDate));
    }

    setFilteredTransactions(filtered);
  };

  return (
    <Container>
      <Balance>
        Balance <span>â‚¹{income - expense}</span>
      </Balance>
      <div>
        <select value={categoryFilter} onChange={handleCategoryFilterChange}>
          <option value="">All Categories</option>
          <option value="Credited">Credited</option>
          <option value="Food">Food</option>
          <option value="Transport">Transport</option>
          <option value="Shopping">Shopping</option>
          <option value="Entertainment">Entertainment</option>
          <option value="Education">Education</option>
          <option value="Other">Other (Mention in details)</option>
        </select>
        <input
          type="date"
          value={startDateFilter}
          onChange={handleStartDateFilterChange}
          placeholder="Start Date"
        />
        <input
          type="date"
          value={endDateFilter}
          onChange={handleEndDateFilterChange}
          placeholder="End Date"
        />
      </div>
      <AddBtn onClick={() => setToggle(!toggle)}>
        {toggle ? "Cancel" : "Add"}
      </AddBtn>
    </Container>
  );
};

export default OverviewComponent;
