import React, { useState } from "react";
import styled from "styled-components";
import AddTransaction from "./AddTransaction";
import OverviewComponent from "./OverviewComponent";
import TransactionsContainer from "./TransactionsContainer";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 30px 0 10px;
  width: 360px;
  font-family: Montserrat;
`;

const HomeComponent = () => {
  const [transactions, updateTransaction] = useState([]);
  const [toggle, setToggle] = useState(false);

  const AddTransactions = (payload) => {
    const transactionsArray = [...transactions];
    transactionsArray.push(payload);
    updateTransaction(transactionsArray);
  };

  const removeTransaction = (id) => {
    const updatedTransactions = transactions.filter((transaction) => transaction.id !== id);
    updateTransaction(updatedTransactions);
  };

  return (
    <Container>
      <OverviewComponent setToggle={setToggle} transactions={transactions} />

      {toggle && <AddTransaction setToggle={setToggle} AddTransactions={AddTransactions} />}

      <TransactionsContainer
        transactions={transactions}
        removeTransaction={removeTransaction}
      />
    </Container>
  );
};

export default HomeComponent;
